            if (books.map(book => book._id).includes(book._id)) {
                //delete
            } else {
                API.saveBook(book)
                    .then(book => setBooks(books.push([book])))
                    .catch(err => console.error(err));
            }
        } */


    return (
        <div>
            <Card key={id} className='border rounded-sm my-3'>
                <Container className='p-3' style={{ width: 660, height: 'auto' }}>
                    <Row>
                        <Col>
                            <Card>
                                <Card.Img alt={title} src={thumbnail} />
                            </Card>
                        </Col>
                        <Col className='mr-3'>
                            <Card.Body className='text-truncate'>
                                <h2>{title}</h2>
                                <h4> by {authors}</h4>
                                <p className='mt-3'>{description}</p>
                            </Card.Body>
                            <Button href={infoLink}>View</Button>
                            <Button>Save</Button>
                        </Col>
                    </Row>
                </Container>
            </Card>
        </div>
    )
}

export default BookTile;
