          .catch(err => {
            setLoading(true);
            console.log(err.response);
          });
      };

    // Main Show Case
    const mainHeader = () => {
      return (
        <Jumbotron>
        <Row>
          <Col md={3} />
          <Col md={6}>
          <Form>
            <Form.Group size='lg' className='mb-3'>
              <Form.Control
                placeholder='Book Search'
                value={query}
                onChange={e => setQuery(e.target.value)}
              />
                <Button color='secondary' onClick={handleSubmit}>
                  <i className='fas fa-search'></i>
                </Button>
            </Form.Group>
            </Form>
            </Col>
