import React from 'react';
import { Form, Button, ButtonGroup } from 'react-bootstrap';
import API from "../utils/API";


function Searchbar() {

    const [search, setSearch] = useState({});
    //const [books, setBooks] = useState([]);

    function handleFormSubmit(event) {
        event.preventDefault();
        API.getBook(query)
            .then(res => 
                setSearch(res.data)
            )
            .catch(err => console.log(err));
        console.log(search);
      };

      function handleInputChange(event) {
        const { name, value } = event.target;
        setSearch({...search, [name]: value})
      };

    return (

                <Form>
                    <Form.Group>
                        <Form.Control type='text' placeholder='Search books' onChange={handleInputChange}/>
                        <ButtonGroup className='mx-auto'>
                            <Button onClick={() => handleFormSubmit} type="submit" class="btn btn-default">Author Search</Button>
                            <Button onClick={() => handleFormSubmit}  type="submit" class="btn btn-default">Title Search</Button>
                        </ButtonGroup>
                    </Form.Group>
                </Form>

    );

}

export default Searchbar;
