import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import BookTile from '../components/BookTile.js';
import { Container, Jumbotron, Form, Button, Spinner, Row, Col, CardColumns } from "react-bootstrap";

const Main = () => {

  // States
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [tiles, setTiles] = useState([]);
  // Handle Search
  const handleSubmit = () => {
    API.getBook(query)
      .then(res => {

        setTiles(res.data.items);
        setLoading(false);
      })
      .catch(err => {
        console.log(err.response);
      });
  };

  // Main Show Case
  const mainHeader = () => {
    return (
      <Jumbotron>
        <Row>
          <Col md={3} />
          <Col md={6}>
            <Form>
              <Form.Group size='lg' className='mb-3'>
                <Form.Control
                  placeholder='Book Search'
                  value={query}
                  onChange={e => setQuery(e.target.value)}
                />
                <Button color='secondary' onClick={handleSubmit}>
                  <i className='fas fa-search'></i>
                </Button>
              </Form.Group>
            </Form>
          </Col>
          <Col md={3} />
        </Row>
      </Jumbotron>

    );
  };

  const handleTiles = () => {
    const items = tiles.map((item, i) => {
      let thumbnail = '';
      if (item.volumeInfo.imageLinks) {
        thumbnail = item.volumeInfo.imageLinks.thumbnail;
      }

      return (
        <Row>
          <BookTile
            id={item.volumeInfo.id}
            thumbnail={thumbnail}
            title={item.volumeInfo.title}
            language={item.volumeInfo.language}
            authors={item.volumeInfo.authors}
            description={item.volumeInfo.description}
            infoLink={item.volumeInfo.infoLink}
          />
        </Row>
      );
    });
    return (
      <Row>{items}</Row>
    );
  };
  //};
  return (
    <div>
      {mainHeader()}
      <Container>
        <Col md={4}/>
        <Col md={4}>{handleTiles()}</Col>
        <Col md={4}/>
      </Container>
    </div>
  );
}


