import React, { useState } from 'react';
import { Card, Row, Col, Button, Container } from 'react-bootstrap';
import API from '../utils/API';

const BookTile = ({ id, title, thumbnail, authors, description, infoLink }) => {

     const [books, setBooks] = useState([]);

        function handleSave(book) {
            if (books.map(book => book._id).includes(book._id)) {
                API.deleteBook(book)
                .then(book => setBooks(books.push([book])))
                .catch(err => console.error(err));               
            } else {
                API.saveBook(book)
                    .then(book => setBooks(books.push([book])))
                    .catch(err => console.error(err));
            }
        } 


    return (
        <div>
            <Card key={id} className='border rounded-sm my-3'>
                <Container className='p-3' style={{ width: 660, height: 'auto' }}>
                    <Row>
                        <Col>
                            <Card>
                                <Card.Img alt={title} src={thumbnail} href={infoLink}/>
                            </Card>
                        </Col>
                        <Col>
                            <Card.Body className={{ overflow: 'hidden', whiteSpace: 'nowrap', textOverflow: 'ellipsis' }}>
                                <h2>{title}</h2>
                                <h4> by {authors}</h4>
                                <p className='mt-3'>{description}</p>
                            </Card.Body>
                            <Button onclick={handleSave}>Save</Button>
                        </Col>
                    </Row>
                </Container>
            </Card>
        </div>
    )
}

export default BookTile;
