import React, { useEffect, useState } from 'react';
import { Card, Row, Col, Button } from 'react-bootstrap';
import API from '../utils/API';

function Results() {
const [books, setBooks] = useState([]);

useEffect(() => {
API.savedBooks()
    .then(saved => setBooks(saved))
    .catch(err => console.error(err));
});

function handleSaveBook(book) {
    if (books.map(book => book._id).includes(book._id)) {
        //delete
    } else {
        API.saveBook(book)
        .then(book => setBooks(books.push([book])))
        .catch(err => console.error(err));
    }
}


    return(
        <div>
            {!books.length ? (
            <h1 className='text-center'>No Results to Display</h1>
            ) : (
                <div>
                    {books.map(res => (
                        <Card key={res._id}>
                            <Row>
                                <Col>
                                <img alt={res.title} className='img-fluid' src={res.image}/>
                                </Col>
                                <Col>
                                <Card.Body>
                                    <Card.Title>{res.title} by {res.authors}</Card.Title>
                                        <Card.Text>{res.description}</Card.Text>
                                </Card.Body>
                                <Button href={res.link}>Details</Button>
                                <Button onClick={handleSaveBook}>Save</Button>
                                </Col>
                            </Row>
                        </Card>
                    ))}
                </div>
            )}
        </div>
    )
}

export default Results;
